const express = require('express')
const streamifier = require('streamifier');
const app = express()
const fs = require("fs");

app.get('/stream',(req, res) => {

    // path to image
    const image = fs.readFileSync('/home/sam/Desktop/stream/image1.jpg');

    // creating buffer from image
    const BufferImage = new Buffer.from(image).toString('base64');

    // creeating base64 string from buffer
    const base64Image = `data:image/jpeg;base64,${BufferImage}`;

    // other base64 image example
    // const imagen="data:image/png;base64,";

    // deleting first part of base64 string 
    const data = base64Image.replace(/^data:image\/\w+;base64,/, "");

    // creating buffer from base64 string
    const buffer = Buffer.from(data, 'base64');

    // streaming buffer
    streamifier.createReadStream(buffer).pipe(res);
})

const port = 3000;
app.listen(port, () => {
    console.log(`app listening on port ${port}`);
});
